import React from "react";


const FilmDescription = ({ title,  release_date, popularity, overview, genres,poster_path}) => (
    <>
     
        <div>
        <img
            alt="film"
            src={'https://image.tmdb.org/t/p/w300_and_h450_bestv2' + poster_path}
          />
            <h3>{title} ({release_date}) </h3>
            <p>Popularity <span>{popularity}</span></p>
            <h4>Overview</h4>
            <p>
                {overview}
            </p>
            <h4>Genres</h4>
            <ul>
            {genres.map((genre) =>
                <li key={genre.id}>
                     {genre.name}
                </li>
            )}
</ul>
        </div>
    </>
)

export default FilmDescription
