import React, { Component } from "react";
import {getPopular} from "../services/MovieApi";
import MovieList from "../components/movieList/MovieList";
import queryString from "query-string";

class HomePage extends Component {
  state = {
    popular:[],
    qwery: "",
  };

  componentDidMount() {
    getPopular().then(data => this.setState({ popular: data, locationFromHome:this.props.location.search }));
   
  }

  render() {
    console.log("home", this.props);
    const parsed = queryString.parse(this.props.location.search);
    console.log("parsed", parsed);
    const { popular, qwery } = this.state;
    return (
      <>
        {popular && (
          <MovieList {...this.props} popular={popular} qwery={qwery} />
        )}
      </>
    );
  }
}

export default HomePage;
