import React, { Component } from "react";
import { Route, Link, Switch } from "react-router-dom";
import { getMovie } from "../services/MovieApi";
import queryString from "query-string";
import axios from "axios";
import Cast from "../pages/Cast";
import Reviews from "../pages/Reviews";

class MovieDetailsPage extends Component {
  state = {
    movie: [],
    info: null,
    title: '',
    popularity: '',
    overview:'',
    release_date: '',
    genres:[]
  };

  componentDidMount() {
    const id = this.props.match.params.movieId;
    getMovie(id).then(movie => this.setState({movie: movie.data}))
    
  }
  goBack = () => {
    this.props.history.push("/", this.state.info);
  };

  render() {
    // const test = { title, popularity, overview, release_date, genres=[]}
    const { movie ,title, popularity, overview, release_date, genres=[]} = this.state;
    return (
      <>
        <div>
            <h3>{title} ({release_date}) </h3>
            <p>Popularity <span>{popularity}</span></p>
            <h4>Overview</h4>
            <p>
                {overview}
            </p>
            <h4>Genres</h4>
            {genres.map((genre) =>
                <li key={genre.id}>
                     {genre.name}
                </li>
            )}

        </div>
   
     { movie && (
        <div>
          <img
            alt="film"
            src={`https://image.tmdb.org/t/p/w300_and_h450_bestv2${movie.poster_path}`}
          />
          <Link
            to={{
              pathname: `/movies/${this.props.match.params.movieId}/cast`,
            }}
          >
            Cast
          </Link>
          {this.state.info ? (
            <Link
              to={{
                pathname: `${this.state.info.from}`,
                state: {
                  qwery: this.state.info.qwery,
                },
                search: this.state.info.qwery
                  ? `?qwery=${this.state.info.qwery}`
                  : "",
              }}
            >
              Go back link
            </Link>
          ) : (
            <Link
              to={{
                pathname: "/",
              }}
            >
              Go back link
            </Link>
          )}
          <Link
            to={{
              pathname: `${this.props.match.url}/review`,
            }}
          >
            Review
          </Link>
          <Switch>
            <Route exact path={`/movies/:movieId/cast`} component={Cast} />
            <Route exact path={"/movies/:movieId/review"} component={Reviews} />
          </Switch>
        </div>
      )}
      </>
    );
  }
}

export default MovieDetailsPage;
